mapper.select:
SELECT 
FROM student


        AND (name LIKE CONCAT('%', #{query.keyword}, '%')
        OR student_no LIKE CONCAT('%', #{query.keyword}, '%')
        OR phone LIKE CONCAT('%', #{query.keyword}, '%')
        OR class_name LIKE CONCAT('%', #{query.keyword}, '%'))


        AND student_no = #{query.studentNo}


        AND name LIKE CONCAT('%', #{query.name}, '%')


        AND gender = #{query.gender}


        AND class_name = #{query.className}


ORDER BY id DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM student


        AND (name LIKE CONCAT('%', #{query.keyword}, '%')
        OR student_no LIKE CONCAT('%', #{query.keyword}, '%')
        OR phone LIKE CONCAT('%', #{query.keyword}, '%')
        OR class_name LIKE CONCAT('%', #{query.keyword}, '%'))


        AND student_no = #{query.studentNo}


        AND name LIKE CONCAT('%', #{query.name}, '%')


        AND gender = #{query.gender}


        AND class_name = #{query.className}

mapper.update:
UPDATE student
student_no = #{studentNo}, name = #{name}, gender = #{gender}, age = #{age}, class_name = #{className}, phone = #{phone}, email = #{email}, address = #{address}, update_time = #{updateTime}
WHERE id = #{id}
